mapper.select:
select
	cn_notebook_id as id,
	cn_notebook_name as name
from
	${tableName}
where
	cn_user_id=#{userId}

		limit
			#{start},#{rows}

select
	cn_notebook_id as "id",
	cn_notebook_name as name
from
	${tableName}
where
	cn_user_id = #{userId}   
	and
	cn_notebook_status_id = #{statusId}

		limit
			#{start},#{rows}
